            <p className="publication-name" style={{ color: theme.text }}>
              {repo.name}
            </p>
          </div>
          <div className="publication-details">
            <p
              className="publication-creation-date subTitle"
              style={{ color: theme.secondaryText }}
            >
              Published on {repo.createdAt}
